mapper.select:
SELECT 

FROM sams_order a

WHERE a.id = #{id}

SELECT
FROM sams_order a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sams_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sams_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
